const POINTERHOUR=document.querySelector("#hour"),POINTERMINUTE=document.querySelector("#minute"),POINTERSECOND=document.querySelector("#second");function runClock(){var e=new Date;console.log(e);let t=e.getHours(),o=e.getMinutes(),r=e.getSeconds();console.log("Hora: "+t+" Minuto: "+o+" Segundo: "+r);let n=360*t/12+6*o/12,l=360*o/60+6*r/60,u=360*r/60;POINTERHOUR.style.transform="rotate("+n+"deg)",POINTERMINUTE.style.transform="rotate("+l+"deg)",POINTERSECOND.style.transform="rotate("+u+"deg)"}var intervalo=setInterval(runClock,1e3);